fn main() {
    println!("Hello, advent!");
    let dirs = vec![
        "",
        "",
        "",
        "",
        "",
    ];

    let up      = [1,2,3,1,2,3,4,5,6];
    let down    = [4,5,6,7,8,9,7,8,9];
    let left    = [1,1,2,4,4,5,7,7,8];
    let right   = [2,3,3,5,6,6,8,9,9];

    let mut code = 0;
    let mut btn = 5;

    for digit in dirs {
        for instr in digit.chars() {
            btn = match instr {
                'U' => up[btn-1],
                'D' => down[btn-1],
                'L' => left[btn-1],
                'R' => right[btn-1],
                _ => btn
            }
        }
        code = 10*code + btn;
    }

    println!("{}", code);

    let up      = [1,2,1,4,5,2,3,4,9,6,7,8,0xB];
    let down    = [3,6,7,8,5,0xA,0xB,0xC,9,0xA,0xD,0xC,0xD];
    let left    = [1,2,2,3,5,5,6,7,8,0xA,0xA,0xB,0xD];
    let right   = [1,3,4,4,6,7,8,9,9,0xB,0xC,0xC,0xD];

    let mut code = 0;
    let mut btn = 5;

    for digit in dirs {
        for instr in digit.chars() {
            btn = match instr {
                'U' => up[btn-1],
                'D' => down[btn-1],
                'L' => left[btn-1],
                'R' => right[btn-1],
                _ => btn
            }
        }
        code = 16*code + btn;
    }

    println!("{:X}", code);
}
